package main

import (
	"fmt"
	"tcpip"
)

func main() {

	initPacketByte := tcpip.StrtoByte("")
	protectedInit := tcpip.ParseRawQuicPacket(initPacketByte, true).(tcpip.InitialPacket)
	//fmt.Printf("%+v\n", protectedInit)

	keyblock := tcpip.CreateQuicInitialSecret(protectedInit.DestConnID)

	initPacketByte = tcpip.ToUnprotecdQuicPacket(protectedInit, initPacketByte, keyblock)

	//ヘッダ保護を解除したパケットをパースする
	unprotectInit := tcpip.ParseRawQuicPacket(initPacketByte, false).(tcpip.InitialPacket)
	//fmt.Printf("%x\n", unprotectInit.Payload)

	plaintext := tcpip.DecryptQuicPayload(unprotectInit.PacketNumber, initPacketByte[0:22], unprotectInit.Payload, keyblock)

	i := tcpip.ParseQuicFrame(plaintext).(tcpip.QuicCryptoFrame)
	fmt.Printf("Data is %x\n", i.Data)
}

